def part1():
	x = 1
	y = 1
	code = ''

	for line in lines:
		for c in line:
			if c == 'U':
				y = max(0, y - 1)
			elif c == 'D':
				y = min(2, y + 1)
			elif c == 'L':
				x = max(0, x - 1)
			elif c == 'R':
				x = min(2, x + 1)
		code += str((y * 3) + x + 1)
	print(code)

